import React from "react";
import { storiesOf } from "@storybook/react";
import EditorialHalfImage from "./EditorialHalfImage";
import RandomImage from "../../../helpers/RandomImage";
import { EditorialHeadlineStory } from "../../../helpers/Editorial/EditorialHeadline/EditorialHeadline.story";

const EditorialHalfImageStory = (
    <EditorialHalfImage
        image={RandomImage.getPortrait()}
        imageFirst
        element={EditorialHeadlineStory}
        elementPosition={"bottom"}
    />
);

storiesOf("EditorialHalfImage").add("default", () => EditorialHalfImageStory);

EditorialHalfImage.testOptions.elementPosition.forEach(val => {
    EditorialHalfImage.testOptions.imageFirst.forEach(val2 => {
        storiesOf("EditorialHalfImage").add(
            `${val}${val2 ? ", imageFirst" : ""}`,
            () => (
                <EditorialHalfImage
                    image={RandomImage.getPortrait()}
                    element={EditorialHeadlineStory}
                    elementPosition={val}
                    imageFirst={val2}
                />
            )
        );
    });
});

export default EditorialHalfImageStory;
