import React from "react";
import PropTypes from "prop-types";
import "./EditorialHeadline.scss";

function EditorialHeadline(props) {
    let _classes = ["EditorialHeadline"];

    if (props.textAlign === "center") {
        _classes.push("EditorialHeadline--alignCenter");
    } else if (props.textAlign === "right") {
        _classes.push("EditorialHeadline--alignRight");
    }
    if (props.themeWhite) {
        _classes.push("EditorialHeadline--themeWhite");
    }
    return (
        <div className={_classes.join(" ")}>
            <div className="EditorialHeadline__title">{props.title}</div>
            <div className="EditorialHeadline__paragraph">
                {props.paragraph}
            </div>
        </div>
    );
}
EditorialHeadline.testOptions = {
    textAlign: ["left", "center", "right"]
};
EditorialHeadline.propTypes = {
    title: PropTypes.string,
    paragraph: PropTypes.string,
    textAlign: PropTypes.oneOf(EditorialHeadline.testOptions.textAlign),
    themeWhite: PropTypes.boolean
};
export default EditorialHeadline;
