import React from "react";
import PropTypes from "prop-types";
import "./EditorialHeadlineNike.scss";

function EditorialHeadlineNike(props) {
    let _classes = ["EditorialHeadlineNike"];

    if (props.textAlign === "center") {
        _classes.push("EditorialHeadlineNike--alignCenter");
    } else if (props.textAlign === "right") {
        _classes.push("EditorialHeadlineNike--alignRight");
    }

    return (
        <div className={_classes.join(" ")}>
            <div className="EditorialHeadlineNike__title">{props.title}</div>
            <div className="EditorialHeadlineNike__paragraph">
                {props.paragraph}
            </div>
        </div>
    );
}
EditorialHeadlineNike.testOptions = {
    textAlign: ["left", "center", "right"]
};
EditorialHeadlineNike.propTypes = {
    title: PropTypes.string,
    paragraph: PropTypes.string,
    textAlign: PropTypes.oneOf(EditorialHeadlineNike.testOptions.textAlign)
};
export default EditorialHeadlineNike;
