import React from "react";
import PropTypes from "prop-types";
import "./EditorialIntroNike.scss";

function EditorialIntroNike(props) {
    let _classes = ["EditorialIntroNike"];

    return (
        <div className={_classes.join(" ")}>
            <div className="container">
                <div className="EditorialIntroNike__inner">
                    {props.title && (
                        <div className="EditorialIntroNike__title">
                            {props.title}
                        </div>
                    )}
                    {props.subTitle && (
                        <div className="EditorialIntroNike__subTitle">
                            {props.subTitle}
                        </div>
                    )}
                    {props.text && (
                        <div className="EditorialIntroNike__text">
                            {props.text}
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
}

EditorialIntroNike.propTypes = {
    title: PropTypes.string,
    text: PropTypes.string,
    subTitle: PropTypes.string
};
export default EditorialIntroNike;
