import React from "react";
import PropTypes from "prop-types";
import LazyAssetWrapper from "../../../core/LazyAssetWrapper/LazyAssetWrapper";
import "./ProductVariants.scss";

function ProductVariants(props) {
    return (
        <div className="ProductVariants">
            {props.variants.map((variant, index) => {
                let _className = "ProductVariants__variant";
                if (variant.isActive) {
                    _className += " ProductVariants__variant--isActive";
                }
                return (
                    <div className={_className} key={index}>
                        <LazyAssetWrapper
                            className={"ProductVariants__photo"}
                            mode={"natural"}
                            crop={"portrait"}
                            sizes={`200px`}
                            image={variant.image}
                            // placeholder={null}
                            // backgroundColor={"white"}
                        />
                    </div>
                );
            })}
        </div>
    );
}

ProductVariants.propTypes = {
    variants: PropTypes.array.isRequired
};

export default ProductVariants;
