import React from "react";
import PropTypes from "prop-types";
import LazyAssetWrapper from "../../../core/LazyAssetWrapper/LazyAssetWrapper";
import "./ProductVariantsNike.scss";

function ProductVariantsNike(props) {
    return (
        <div className="ProductVariantsNike">
            {props.variants.map((variant, index) => {
                let _className = "ProductVariantsNike__variant";
                if (variant.isActive) {
                    _className += " ProductVariantsNike__variant--isActive";
                }
                return (
                    <div className={_className} key={index}>
                        <LazyAssetWrapper
                            className={"ProductVariantsNike__photo"}
                            mode={"natural"}
                            crop={"square"}
                            sizes={`200px`}
                            image={variant.image}
                            // placeholder={null}
                            // backgroundColor={"white"}
                        />
                    </div>
                );
            })}
        </div>
    );
}

ProductVariantsNike.propTypes = {
    variants: PropTypes.array.isRequired
};

export default ProductVariantsNike;
