import React from "react";
import PropTypes from "prop-types";
import "./SizePickerNike.scss";

function SizePickerNike(props) {
    return (
        <div className="SizePickerNike">
            <div className="SizePickerNike__head">
                <div className="SizePickerNike__title">Select Size</div>
                <div className="SizePickerNike__link">
                    <a href={""}>Size guide</a>
                </div>
            </div>
            <div className="SizePickerNike__sizes">
                {props.sizes.map((option, index) => {
                    return (
                        <div className="SizePickerNike__option" key={index}>
                            <input
                                className="SizePickerNike__option__radio"
                                disabled={!option.available}
                                type="radio"
                                id={option.value}
                                name={props.name}
                                value={option.value}
                            />
                            <label
                                htmlFor={option.value}
                                className={"SizePickerNike__option__label"}
                                title={option.tooltip}
                            >
                                {option.label}
                            </label>
                        </div>
                    );
                })}
            </div>
        </div>
    );
}

SizePickerNike.propTypes = {
    sizes: PropTypes.array.isRequired,
    name: PropTypes.string.isRequired
};

export default SizePickerNike;
